import Foundation
import UIKit

struct TastyFoodResults:Codable{
    var results:[TastyFood]?;
}

struct TastyFood:Codable, Identifiable{
    var id:Int?;
    var country:String?;//Good
    var total_time_minutes:Int?//Changed : G
    var total_time_tier:Time_tier?;//G
    var instructions:[Steps]?;//G
    var thumbnail_url:String?;//G
    var original_video_url:String?;//G
//    var video_url : String?;//G
    var sections:[Section]?;//G
    var name:String?;//G
    var yields:String?;//G
    var credits:[Credit]?;//G
    var tags:[Tag]?;//G
    var prep_time_minutes:Int?;//Changed G
    var nutrition:Nutrition?;//G
    var description:String?;
}

struct Tag:Codable,Identifiable{
    var id:Double?;// Changed G
    var name:String?;//G
    var display_name:String?;//G
    var type:String?;//G
}

struct Credit:Codable{
    var name:String?;//G
}

struct Section:Codable{
    var name:String?;
    var position:Int?;
    var components:[Component]?;//G
}

struct SectionID:Identifiable{
    var id:String;
    var section:Section?;
    var color:UIColor;
}

struct Component:Codable,Identifiable{
    var ingredient:Ingredient?;//G
    var id:Double?;// Changed G
    var raw_text:String?;//G
    var extra_comment:String?;//G
    var position:Int?;//G
    var measurements:[Quantity]?;//G
}

struct Quantity:Codable{
    var quantity:String?;//G
    var unit:Unit?;//G
}

struct Unit:Codable{
    var name:String?;//G
    var abbreviation:String?;//G
    var display_plural:String?;//G
}

struct Ingredient:Codable,Identifiable{
    var id : Int?;//G
    var name:String?;//G
    var display_singular:String?;//G
    var display_plural:String?;//G
}

struct Nutrition:Codable{
    var fiber:Int?;//G
    var protein:Int?;//G
    var fat:Int?;//G
    var calories:Int?;//G
    var sugar:Int?;//G
    var carbohydrates:Int?;//G
}


struct Steps:Codable,Identifiable{
    var position:Int?;//G
//    var start_time:Int?;//G
//    var end_time:Int?;//G
    var temperature:Int?;//?
    var id:Int?;//Changed : G
    var display_text:String?;//G
};


struct Time_tier:Codable {
    var display_tier:String?;//G
    var tier:String?;//G
}

